let slideIndex=1,counterElem=document.querySelector(".box__counter_current");function nextSlide(){showSlides(slideIndex+=1)}function previousSlide(){showSlides(slideIndex-=1)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){let t=document.querySelectorAll(".slider__item");e>t.length&&(slideIndex=1),e<1&&(slideIndex=t.length);for(let e of t)e.style.display="none";t[slideIndex-1].style.display="block"}showSlides(slideIndex),document.querySelector(".box__right").addEventListener("click",(()=>{nextSlide(),counterElem.textContent=slideIndex.toString()})),document.querySelector(".box__left").addEventListener("click",(()=>{previousSlide(),counterElem.textContent=slideIndex.toString()})),setInterval((()=>{nextSlide(),counterElem.textContent=slideIndex.toString()}),7e3);