let screenWidth=window.screen.width,counterElem=document.querySelector(".box__counter_current"),counter=1,offline=0;const sliderLine=document.querySelector(".slider__line");function nextImage(){offline+=screenWidth,counter+=1,offline>=4*screenWidth&&(offline=0,counter=1),sliderLine.style.left=-offline+"px",counterElem.textContent=counter.toString()}function prevImage(){offline-=screenWidth,counter-=1,offline<0&&(offline=3*screenWidth,counter=4),sliderLine.style.left=-offline+"px",counterElem.textContent=counter.toString()}window.addEventListener("resize",(()=>{offline=0,counter=1,screenWidth=window.screen.width,sliderLine.style.left=-offline+"px",counterElem.textContent=counter.toString()})),screenWidth>1920&&(screenWidth=1920),setInterval((()=>{nextImage()}),5e3),document.querySelector(".box__right").addEventListener("click",(()=>{nextImage()})),document.querySelector(".box__left").addEventListener("click",(()=>{prevImage()}));